import React from 'react';
import {useState} from 'react';
import { GiphyFetch } from '@giphy/js-fetch-api';

const giphyURL = process.env.REACT_APP_GIPHY_URL
const Giphy = () => {

  const [text, setText] = useState('')
  const [results, setResults] = useState([])
  const [loading, setLoading] = useState(false)

  const handleInput = (e) => {
      setText(e.target.value)
    }

  const handleSubmit = (e) => {
     const url = giphyURL.concat(text)
          if(text.length > 0) {
            setLoading(true);
            fetch(url)
                .then((response) => response.json())
                .then((content) => {
                    setResults(content.data.map((gif) => {
                        return gif.images.fixed_height.url;
                        }))
                })
                .catch((err) => {
                setLoading(false);
                  console.error(err);
                })
          }

      console.log(text)
      console.log(url)

      //change error state back to false
      setText('')
      setLoading(false)
    }

    return (
        <div className="App">

              <h1>Animated Text Generator</h1>
              <h3>Type text into the form and hit submit</h3>

              <input className='input-field'
              placeholder="Search Gifs"
              value={text}
              onChange={handleInput} />
              <button className='submit-btn' onClick={handleSubmit}>Submit</button>
            <div> {
                results.map(({key, gif})=>
                    <img key={key} src={gif}/>
                )
                }
            </div>
        </div>
    );
  }



export default Giphy
